import React from 'react';
import { Link } from 'react-router-dom';
import { FaHome, FaBuilding, FaIndustry } from 'react-icons/fa';

const ServiceCategories: React.FC = () => {
  const categories = [
    {
      icon: <FaHome className="w-12 h-12 text-primary" />,
      title: "Ev Güvenlik",
      features: ["7/24 İzleme", "Mobil Uygulama", "Akıllı Sensörler"],
      price: "199₺'den başlayan fiyatlarla",
      link: "/ev-guvenlik"
    },
    {
      icon: <FaBuilding className="w-12 h-12 text-primary" />,
      title: "İş Yeri Güvenlik",
      features: ["Gelişmiş Kamera Sistemi", "Uzaktan Erişim", "Acil Müdahale"],
      price: "299₺'den başlayan fiyatlarla",
      link: "/isyeri-guvenlik"
    },
    {
      icon: <FaIndustry className="w-12 h-12 text-primary" />,
      title: "Kurumsal Güvenlik",
      features: ["Özel Çözümler", "Entegre Sistemler", "7/24 Teknik Destek"],
      price: "Özel Fiyatlandırma",
      link: "/kurumsal"
    }
  ];

  return (
    <section className="py-20 bg-gray-50">
      <div className="container mx-auto px-4">
        <h2 className="text-3xl font-bold text-center mb-12">Güvenlik Çözümlerimiz</h2>
        
        <div className="grid md:grid-cols-3 gap-8">
          {categories.map((category, index) => (
            <div key={index} className="bg-white rounded-xl shadow-lg p-8 hover:shadow-xl transition">
              <div className="flex justify-center mb-6">{category.icon}</div>
              <h3 className="text-xl font-bold text-center mb-4">{category.title}</h3>
              
              <ul className="space-y-3 mb-6">
                {category.features.map((feature, i) => (
                  <li key={i} className="flex items-center">
                    <span className="w-2 h-2 bg-primary rounded-full mr-2" />
                    {feature}
                  </li>
                ))}
              </ul>
              
              <p className="text-primary font-semibold text-center mb-6">
                {category.price}
              </p>
              
              <Link
                to={category.link}
                className="block text-center bg-primary text-white py-3 rounded-lg hover:bg-primary-dark transition"
              >
                Detaylı Bilgi
              </Link>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default ServiceCategories;
