import React from 'react';
import { FaClipboardCheck, FaTools, FaCheckCircle, FaShieldAlt } from 'react-icons/fa';

const HowItWorks: React.FC = () => {
  const steps = [
    {
      icon: <FaClipboardCheck className="w-10 h-10 text-primary" />,
      title: "Başvuru",
      description: "Online başvurunuzu yapın, uzmanlarımız sizinle iletişime geçsin"
    },
    {
      icon: <FaTools className="w-10 h-10 text-primary" />,
      title: "Kurulum",
      description: "Profesyonel ekibimiz güvenlik sisteminizi kurup test etsin"
    },
    {
      icon: <FaCheckCircle className="w-10 h-10 text-primary" />,
      title: "Aktivasyon",
      description: "Sisteminiz aktif edilsin, mobil uygulamaya erişim sağlayın"
    },
    {
      icon: <FaShieldAlt className="w-10 h-10 text-primary" />,
      title: "7/24 İzleme",
      description: "İzleme merkezimiz güvenliğinizi sürekli takip etsin"
    }
  ];

  return (
    <section className="py-20">
      <div className="container mx-auto px-4">
        <h2 className="text-3xl font-bold text-center mb-12">Nasıl Çalışır?</h2>
        
        <div className="grid md:grid-cols-4 gap-8">
          {steps.map((step, index) => (
            <div key={index} className="text-center">
              <div className="flex justify-center mb-4">{step.icon}</div>
              <h3 className="text-xl font-bold mb-2">{step.title}</h3>
              <p className="text-gray-600">{step.description}</p>
              
              {index < steps.length - 1 && (
                <div className="hidden md:block absolute right-0 top-1/2 transform -translate-y-1/2">
                  <svg className="w-6 h-6 text-gray-300" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 5l7 7-7 7" />
                  </svg>
                </div>
              )}
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default HowItWorks;
